import React, { useEffect } from 'react';
import L from 'leaflet';

import cuisinesMap from '../../api/cuisinesMap.json';


const Mappage = () => {
  useEffect(() => {
    fetch('../../api/cuisinesMap.json')
      .then((response) => response.json())
      .then((data) => {
        // Render the map and markers using the fetched data
        renderMap(data);
      })
      .catch((error) => {
        console.error('Error fetching cuisinesMaps.json:', error);
      });
  }, []);

  const renderMap = (cuisines) => {
    // Initialize the map
    // You can use any map library or component of your choice to render the map

    // Example using Leaflet library
    const map = L.map('map').setView([38.7223, -9.1393], 12); // Lisbon coordinates

    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
      attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
      maxZoom: 18
    }).addTo(map);

    // Render markers for each cuisine
    cuisines.forEach((cuisine) => {
      const marker = L.marker(cuisine.coordinates).addTo(map);

      marker.on('click', () => {
        const popupContent = `
          <h3>${cuisine.name}</h3>
          <p>${cuisine.description}</p>
        `;
        L.popup()
          .setLatLng(cuisine.coordinates)
          .setContent(popupContent)
          .openOn(map);
      });
    });
  };

  return (
    <div>
      <h2>Map Page</h2>
      <div id="map" style={{ height: '500px' }}></div>
    </div>
  );
};

export default Mappage;
